TRUNCATE TABLE BATCH_ALIM_ORG;
INSERT INTO BATCH_ALIM_ORG SELECT * FROM (SELECT 'GENERAL','EQP',HRORG_RESP(MATRIC,TO_CHAR(SYSDATE, 'yyyyMMdd')) AS MATRICPERE,MATRIC FROM SECUMATRIC WHERE (DSFCN IS NULL OR DSFCN >= TO_CHAR(SYSDATE, 'yyyymmdd')) AND DSDCN IS NOT NULL AND MATRIC IN (SELECT DISTINCT MATRIC FROM STD_STRUC_AFFECTATION A WHERE TO_CHAR(SYSDATE, 'yyyymmdd') BETWEEN A.DATE_EFF and A.DATE_FIN)) WHERE MATRICPERE IS NOT NULL;
